import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = ({ user, setView }) => {
  const [requests, setRequests] = useState([]);
  const [form, setForm] = useState({ documentType: '', fullName: '', purpose: '', deliveryMethod: '', address: '' });

  useEffect(() => {
    fetchRequests();
  }, []);

  const fetchRequests = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/my-requests?userId=user::${user.email}`);
      setRequests(res.data);
    } catch (error) {
      console.error('Error fetching requests:', error);
    }
  };

  const submitRequest = async () => {
    try {
      await axios.post('http://localhost:3001/submit-request', { ...form, userId: `user::${user.email}` });
      setForm({ documentType: '', fullName: '', purpose: '', deliveryMethod: '', address: '' });
      fetchRequests();  // Refresh list
    } catch (error) {
      alert('Error submitting request');
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <p>Welcome, {user.fullName}!</p>
      <button onClick={() => setView('admin')}>Admin View</button>
      <div>
        <h3>Submit New Request</h3>
        <select value={form.documentType} onChange={e => setForm({ ...form, documentType: e.target.value })}>
          <option value="">Select Document</option>
          <option>Birth Certificate</option>
          <option>Marriage Certificate</option>
          <option>Death Certificate</option>
          {/* Add more as needed */}
        </select>
        <input placeholder="Full Name" value={form.fullName} onChange={e => setForm({ ...form, fullName: e.target.value })} />
        <input placeholder="Purpose" value={form.purpose} onChange={e => setForm({ ...form, purpose: e.target.value })} />
        <input placeholder="Delivery Method" value={form.deliveryMethod} onChange={e => setForm({ ...form, deliveryMethod: e.target.value })} />
        <input placeholder="Address" value={form.address} onChange={e => setForm({ ...form, address: e.target.value })} />
        <button onClick={submitRequest}>Submit Request</button>
      </div>
      <h3>My Requests</h3>
      <ul>
        {requests.map(r => <li key={r.id}>{r.documentType} - Status: {r.status}</li>)}
      </ul>
    </div>
  );
};

export default Dashboard;
