import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setUser }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [isRegister, setIsRegister] = useState(false);

  const handleSubmit = async () => {
    const endpoint = isRegister ? '/register' : '/login';
    const payload = isRegister ? { email, password, fullName } : { email, password };
    try {
      const res = await axios.post(`http://localhost:3001${endpoint}`, payload);
      if (res.data.user) setUser(res.data.user);
      else alert(res.data.message);
    } catch (error) {
      alert('Error: ' + error.response.data.error);
    }
  };

  return (
    <div>
      <h2>{isRegister ? 'Register' : 'Login'}</h2>
      <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
      <input placeholder="Password" type="password" value={password} onChange={e => setPassword(e.target.value)} />
      {isRegister && <input placeholder="Full Name" value={fullName} onChange={e => setFullName(e.target.value)} />}
      <button onClick={handleSubmit}>{isRegister ? 'Register' : 'Login'}</button>
      <button onClick={() => setIsRegister(!isRegister)}>Switch to {isRegister ? 'Login' : 'Register'}</button>
    </div>
  );
};

export default Login;
