    import React, { useState, useEffect } from 'react';
    import './app.css';
    import Login from './components/Login';
    import Dashboard from './components/Dashboard';
    import Admin from './components/Admin';

    function App() {
      const [user, setUser] = useState(null);
      const [view, setView] = useState('login');
      const [darkMode, setDarkMode] = useState(false);

      useEffect(() => {
        document.body.className = darkMode ? 'dark-mode' : '';
      }, [darkMode]);

      return (
        <div className="container">
          <button className="toggle-dark" onClick={() => setDarkMode(!darkMode)} title="Toggle Dark Mode">
            {darkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
          {user && (
            <nav className="top-nav">
              <div className="logo">
                <div className="mark">S</div>
                <div>SwiftServe</div>
              </div>
              <div className="user-area">
                <div style={{ color: 'white' }}>{user.fullName || user.email}</div>
                <button className="nav-btn" onClick={() => { setUser(null); setView('login'); }}>Logout</button>
              </div>
            </nav>
          )}
          {!user ? (
            <Login setUser={setUser} />
          ) : view === 'dashboard' ? (
            <Dashboard user={user} setView={setView} setUser={setUser} />
          ) : (
            <Admin setView={setView} user={user} setUser={setUser} />
          )}
        </div>
      );
    }

    export default App;
