import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = ({ setView }) => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetchAllRequests();
  }, []);

  const fetchAllRequests = async () => {
    try {
      // Simplified: Fetch all (in real app, use a dedicated endpoint)
      const res = await axios.get('http://localhost:3001/my-requests?userId=all');  // Adjust backend if needed
      setRequests(res.data);
    } catch (error) {
      console.error('Error fetching requests:', error);
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await axios.post('http://localhost:3001/update-status', { id, status });
      fetchAllRequests();  // Refresh
    } catch (error) {
      alert('Error updating status');
    }
  };

  return (
    <div>
      <h1>Admin Panel</h1>
      <button onClick={() => setView('dashboard')}>Back to Dashboard</button>
      <h3>All Requests</h3>
      <ul>
        {requests.map(r => (
          <li key={r.id}>
            {r.documentType} by {r.details?.fullName} - Status: {r.status}
            <button onClick={() => updateStatus(r.id, 'Approved')}>Approve</button>
            <button onClick={() => updateStatus(r.id, 'Rejected')}>Reject</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Admin;
