import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = ({ setView }) => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    fetchAllRequests();
  }, []);

  const fetchAllRequests = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:3001/my-requests?userId=all');
      setRequests(res.data);
    } catch (error) {
      console.error('Error fetching requests:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await axios.post('http://localhost:3001/update-status', { id, status });
      setMessage(`Request ${status.toLowerCase()} successfully!`);
      setIsError(false);
      fetchAllRequests();
    } catch (error) {
      setMessage('Error updating status');
      setIsError(true);
    }
  };

  return (
    <div>
      <h1>‚öôÔ∏è Admin Panel</h1>
      <button onClick={() => setView('dashboard')}>Back to Dashboard</button>
      
      {message && <p className={isError ? 'error' : 'success'}>{message}</p>}
      
      <h3>üìã All Requests</h3>
      {loading ? <p>Loading requests...</p> : (
        <ul>
          {requests.map(r => (
            <li key={r.id}>
              <div>
                <strong>{r.documentType}</strong> by {r.details?.fullName} - <span className={`status ${r.status.toLowerCase()}`}>{r.status}</span>
                <br />
                <small>üìß {r.userId} | üìÖ {new Date(r.submittedAt).toLocaleDateString()}</small>
              </div>
              <div>
                <button onClick={() => updateStatus(r.id, 'Approved')}>‚úÖ Approve</button>
                <button onClick={() => updateStatus(r.id, 'Rejected')}>‚ùå Reject</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Admin;
